["Nameplate Added"] = "function (self, unitId, unitFrame, envTable, modTable)\n    if modTable ~= nil then\n        modTable.updateCastState(unitFrame)\n    end\n    \n    local function OnCastStart(self, event, unit)\n        if unit ~= unitId then return end\n        if not unitFrame.originalLevel then\n            unitFrame.originalLevel = unitFrame:GetFrameLevel()\n            local timeBasedLevel = math.floor(GetTimePreciseSec() * 1000000) % 1000\n            local newFrameLevel = unitFrame.originalLevel + timeBasedLevel\n            unitFrame:SetFrameLevel(newFrameLevel)\n        end\n    end\n    \n    local function OnCastStop(self, event, unit)\n        if unit ~= unitId then return end\n        if unitFrame.originalLevel then\n            unitFrame:SetFrameLevel(unitFrame.originalLevel)\n            unitFrame.originalLevel = nil\n        end\n    end\n    \n    unitFrame:RegisterUnitEvent(\"UNIT_SPELLCAST_START\", unitId)\n    unitFrame:RegisterUnitEvent(\"UNIT_SPELLCAST_STOP\", unitId)\n    unitFrame:RegisterUnitEvent(\"UNIT_SPELLCAST_CHANNEL_START\", unitId)\n    unitFrame:RegisterUnitEvent(\"UNIT_SPELLCAST_CHANNEL_STOP\", unitId)\n    \n    unitFrame:SetScript(\"OnEvent\", function(self, event, ...)\n            if event == \"UNIT_SPELLCAST_START\" or event == \"UNIT_SPELLCAST_CHANNEL_START\" then\n                OnCastStart(self, event, ...)\n            elseif event == \"UNIT_SPELLCAST_STOP\" or event == \"UNIT_SPELLCAST_CHANNEL_STOP\" then\n                OnCastStop(self, event, ...)\n            end\n    end)\nend",
["Target Changed"] = "function (self, unitId, unitFrame, envTable, modTable)\n    if modTable ~= nil then\n        modTable.checkToHide(unitFrame)\n    end\nend\n\n\n",
["Cast Start"] = "function (self, unitId, unitFrame, envTable, modTable)\n    if modTable ~= nil then\n        modTable.updateCastState(unitFrame)\n    end\nend",
["Nameplate Removed"] = "function (self, unitId, unitFrame, envTable, modTable)\n    if modTable ~= nil then\n        modTable.updateCastState(unitFrame)\n    end\nend",
["Nameplate Updated"] = "function (self, unitId, unitFrame, envTable, modTable)\n    if modTable ~= nil then\n          modTable.checkToHide(unitFrame)\n    end\nend",
["Initialization"] = "function (modTable)\n    \n    local customSpells = {\n        \n        -- Eco-Dome Al'dani\n        [1229510] = true,\n        \n        -- Operation: Floodgate\n        [1214780] = true,\n        \n        -- Priory of the Sacred Flame\n        [427356] = true,\n        [444743] = true,\n        \n        -- The Dawnbreaker\n        \n        -- Ara-Kara, City of Echoes\n        [433841] = true,\n        [434802] = true,\n        [448248] = true,\n        \n        -- Tazavesh\n        [355057] = true,\n        [355934] = true,\n        [357260] = true,\n    }\n    \n    local castingUnits = {}\n    \n    local hideUnit = function(unitFrame, needsHide)\n        if modTable.config.useOpacityAlpha then\n            if needsHide then\n                unitFrame:SetAlpha(modTable.config.opacityAlpha)\n            else\n                unitFrame:SetAlpha(1)\n            end\n        else\n            if needsHide then\n                unitFrame:Hide()\n            elseif not unitFrame:IsShown() then\n                unitFrame:Show()\n            end\n        end\n    end\n    \n    local setCastingUnit = function(nameplateToken, isCasting)\n        if castingUnits ~= nil then\n            if nameplateToken ~= nil then\n                castingUnits[nameplateToken] = isCasting\n            end\n            return\n        end\n        castingUnits = {}\n    end\n    \n    modTable.checkToHide = function(unitFrame)\n        local needsHide = false\n        for t, _ in pairs(castingUnits or {}) do\n            if UnitExists(t) then\n                if unitFrame.namePlateUnitToken ~= t then\n                    if not modTable.updateCastState(unitFrame) then\n                        if not modTable.config.hideInCombatOnly then\n                            needsHide = true\n                        else\n                            local unitInCombat = UnitThreatSituation(\"player\", unitFrame.unit) ~= nil or false\n                            needsHide = unitInCombat\n                        end\n                        break\n                    end\n                end\n            else\n                setCastingUnit(t, nil)\n            end\n        end\n        hideUnit(unitFrame, needsHide)\n    end\n    \n    modTable.updateCastState = function(unitFrame)\n        local castBar = unitFrame.castBar\n        if castBar.casting or castBar.channeling then\n            if modTable.config.useCustomSpells then\n                local findCustomSpell = customSpells[castBar.spellID or -1]\n                if findCustomSpell then\n                    setCastingUnit(unitFrame.namePlateUnitToken, findCustomSpell)\n                    return true\n                end\n            else\n                setCastingUnit(unitFrame.namePlateUnitToken, true)\n                return true\n            end\n        end\n        setCastingUnit(unitFrame.namePlateUnitToken, nil)\n        return false\n    end\n    \nend\n\n\n\n",
["Cast Stop"] = "function (self, unitId, unitFrame, envTable, modTable)\n    if modTable ~= nil then\n        modTable.updateCastState(unitFrame)\n    end\nend",
},
