["Hooks"] = {
["Constructor"] = "function (self, unitId, unitFrame, envTable, modTable)\n    local debuffKoef = modTable.config.debuffKoef\n    \n    envTable.DebuffColors = {\n        [164812] = true, -- druid moonfire\n        [228287] = true, -- monk crane\n        [287280] = true, -- paladin glimmer\n        [146739] = true, -- warlock corruption\n        [445474] = true, -- warlock wither\n        [34914] = true -- priest vampiric touch\n    }\n    \n    local function ChangeColor(r, g, b, koef)\n        -- local new_r = r + (1 - r) * koef\n        -- local new_g = g + (1 - g) * koef\n        -- local new_b = b + (1 - b) * koef\n        local new_r = 0.5\n        local new_g = 0.5\n        local new_b = 0.5\n        return new_r, new_g, new_b, 1\n    end\n    \n    function envTable.SetColors()\n        local debuffContainer = unitFrame.BuffFrame.PlaterBuffList\n        \n        if unitFrame.InCombat ~= true then\n            return\n        end\n        \n        if not envTable.DebuffColors or not debuffContainer then\n            return\n        end\n        \n        local r, g, b, a = unitFrame.healthBar:GetStatusBarColor()\n        local lr, lg, lb, la = ChangeColor(r, g, b, debuffKoef)\n        Plater.SetNameplateColor(unitFrame, lr, lg, lb, la)\n        \n        for index, auraIcon in ipairs (debuffContainer) do\n            if auraIcon and auraIcon.spellId then\n                local color = envTable.DebuffColors[auraIcon.spellId]\n                if (color and auraIcon:IsShown() and auraIcon.RemainingTime > 0) then\n                    Plater.SetNameplateColor(unitFrame, r, g, b, a)\n                end\n            end\n        end\n    end\nend",
["Nameplate Updated"] = "function (self, unitId, unitFrame, envTable)\n    envTable.SetColors()\nend",
},
