["Nameplate Updated"] = "function (self, unitId, unitFrame, envTable)\n    envTable.SetColors()\nend",
["Constructor"] = "function (self, unitId, unitFrame, envTable, modTable)\n    local debuffs = modTable.config.debuffs\n    local buffs = modTable.config.buffs\n    \n    envTable.DebuffColors = {\n    }\n    \n    envTable.BuffColors = {\n        [257597] = buffs, -- 2nd boss motherlode add buff\n    }\n    \n    function envTable.SetColors()\n        local debuffContainer = unitFrame.BuffFrame.PlaterBuffList\n        local buffContainer = unitFrame.BuffFrame2.PlaterBuffList\n        \n        if next(envTable.DebuffColors) ~= nil and debuffContainer then\n            for index, auraIcon in ipairs (debuffContainer) do\n                if auraIcon and auraIcon.spellId then\n                    local color = envTable.DebuffColors[auraIcon.spellId]\n                    if (color and auraIcon:IsShown()) then\n                        Plater.SetNameplateColor(unitFrame, color)\n                    end\n                end\n            end\n        end\n        \n        if next(envTable.BuffColors) ~= nil and buffContainer then\n            for index, auraIcon in ipairs (buffContainer) do\n                if auraIcon and auraIcon.spellId then\n                    local color = envTable.BuffColors[auraIcon.spellId]\n                    if (color and auraIcon:IsShown()) then\n                        Plater.SetNameplateColor(unitFrame, color)\n                    end\n                end\n            end\n        end\n    end\nend",
